mapper.sql:

WHERE 1=1

    AND tcu.ID = #{userId}


    AND tci.CREATE_TIME >= #{filter.createFrom}


    AND tci.CREATE_TIME <= #{filter.createTo}


    AND tci.UPDATE_TIME >= #{filter.updateFrom}


    AND tci.UPDATE_TIME <= #{filter.updateTo}


    AND (
        tci.TITLE LIKE CONCAT('%', #{filter.keyword}, '%')
        OR tci.DESCRIPTION LIKE CONCAT('%', #{filter.keyword}, '%')
    )

ORDER BY tci.CREATE_TIME DESC
