mapper.select:
WITH TAG_FILTERED_TABLE AS ( SELECT DISTINCT tct.CONTENT_ID AS ID FROM TOBE_CONTENT_TAG tct JOIN TOBE_TAG_INFO tti ON tti.ID = tct.TAG_ID WHERE 1=1 AND tti.DELETED=0 AND tti.ID IN #{tag} )
SELECT tcgi.*, CONCAT(tcu.FIRST_NAME, ' ', tcu.LAST_NAME) as OWNER_NAME, tcu.AVATAR_URL, tca.* FROM TOBE_CONTENT_GENERAL_INFO tcgi JOIN TOBE_CORE_USER tcu ON tcgi.OWNER_ID = tcu.ID
JOIN TAG_FILTERED_TABLE tfat ON tfat.ID = tcgi.ID
JOIN TOBE_CONTENT_ADMIN tca ON tca.CONTENT_ID = tcgi.ID WHERE tcgi.PUBLIC_TO_ALL=1 AND tcgi.DELETED=0
AND tcgi.OWNER_ID = #{ownerId}
AND tcgi.CONTENT_TYPE = #{contentType}
AND tcgi.TOPIC = #{topic}
AND (tcgi.TITLE LIKE CONCAT('%', #{keyword}, '%') OR tcgi.DESCRIPTION LIKE CONCAT('%', #{keyword}, '%'))
AND tca.BANNED = false ORDER BY tcgi.CREATE_TIME DESC, tcgi.VIEW_COUNT DESC, tcgi.UPDATE_TIME DESC

WITH COUNT_TABLE AS ( SELECT tct.TAG_ID, count(*) as COUNT FROM TOBE_CONTENT_TAG tct JOIN TOBE_CONTENT_GENERAL_INFO tcgi ON tct.CONTENT_ID = tcgi.ID AND tcgi.CONTENT_TYPE = #{contentType}
AND tcgi.TOPIC = #{topic}
AND (tcgi.TITLE LIKE CONCAT('%', #{keyword}, '%') OR tcgi.DESCRIPTION LIKE CONCAT('%', #{keyword}, '%'))
JOIN TOBE_CONTENT_ADMIN tca ON tca.CONTENT_ID = tcgi.ID WHERE tcgi.DELETED=0 AND tcgi.PUBLIC_TO_ALL=1 AND tca.BANNED=false
AND tcgi.OWNER_ID = #{ownerId}
GROUP BY tct.TAG_ID ) SELECT tti.KEYWORD as LABEL, tti.ID as VALUE, ct.COUNT FROM TOBE_TAG_INFO tti JOIN COUNT_TABLE ct ON ct.TAG_ID = tti.ID AND tti.DELETED=0 ORDER BY ct.COUNT DESC, tti.KEYWORD DESC
